import React from "react";
import Button from "@material-ui/core/Button";
import IconButton from "@material-ui/core/IconButton";
import DeleteIcon from "@material-ui/icons/Delete";
import {
  createMuiTheme,
  withStyles,
  makeStyles,
  ThemeProvider,
} from "@material-ui/core/styles";
import { pink, purple } from "@material-ui/core/colors";

const ColorButton = withStyles((theme) => ({
  root: {
    color: theme.palette.getContrastText(purple[500]),
    backgroundColor: purple[500],
    "&:hover": {
      backgroundColor: purple[700],
    },
  },
}))(Button);

const useStyles = makeStyles((theme) => ({
  margin: {
    margin: theme.spacing(1),
  },
}));

const theme = createMuiTheme({
  palette: {
    primary: pink[200],
    secondary: pink[100],
  },
});

const App = () => {
  return <Counter />;
};

class Counter extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      count: 0,
    };
  }
  ClickCount(n) {
    const theCount = this.state.count + n;
    this.setState({ count: theCount });
  }
  ClickCountReset() {
    this.setState({ count: 0 });
  }
  render() {
    return (
      <div class="counter">
        <h1 class="counter-tittle">Reactカウンター</h1>
        <div>
          <ColorButton variant="contained" color="primary">
            Custom CSS
          </ColorButton>
          <ThemeProvider theme={theme}>
            <Button variant="contained" color="primary">
              Theme Provider
            </Button>
          </ThemeProvider>
          <BootstrapButton variant="contained" color="primary" disableRipple>
            Bootstrap
          </BootstrapButton>
        </div>
        <div class="counter-value-wrap">
          現在のカウント
          <br />
          <span class="counter-value">{this.state.count}</span>
        </div>
        <button
          class="counter-button p10"
          onClick={() => {
            this.ClickCount(10);
          }}
        >
          +10
        </button>
        <button
          class="counter-button p1"
          onClick={() => {
            this.ClickCount(1);
          }}
        >
          +
        </button>
        <button
          class="counter-button m1"
          onClick={() => {
            this.ClickCount(-1);
          }}
        >
          -
        </button>
        <button
          class="counter-button m10"
          onClick={() => {
            this.ClickCount(-10);
          }}
        >
          -10
        </button>
        <IconButton
          aria-label="delete"
          class="reset"
          onClick={() => {
            this.ClickCountReset();
          }}
        >
          <DeleteIcon fontSize="large" />
        </IconButton>
      </div>
    );
  }
}

export default App;
