import React from "react";

const App = () => {
  return <CountUp />;
};

class CountUp extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      count: 0,
    };
  }
  ClickCount(n) {
    const theCount = this.state.count + n;
    this.setState({ count: theCount });
  }
  ClickCountReset() {
    this.setState({ count: 0 });
  }
  render() {
    return (
      <div class="counter">
        <h1 class="counter-tittle">Reactカウンター</h1>
        <div class="counter-value-wrap">
          現在のカウント
          <br />
          <span class="counter-value">{this.state.count}</span>
        </div>
        <button
          class="counter-button"
          onClick={() => {
            this.ClickCount(10);
          }}
        >
          +10
        </button>
        <button
          class="counter-button p1"
          onClick={() => {
            this.ClickCount(1);
          }}
        >
          +
        </button>
        <button
          class="counter-button"
          onClick={() => {
            this.ClickCount(-1);
          }}
        >
          -
        </button>
        <button
          class="counter-button"
          onClick={() => {
            this.ClickCount(-10);
          }}
        >
          -10
        </button>
        <button
          class="counter-button"
          onClick={() => {
            this.ClickCountReset();
          }}
        >
          Reset
        </button>
      </div>
    );
  }
}

export default App;
