import React from "react";

const App = () => {
  return <CountUp />;
};

class CountUp extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      count: 0,
    };
  }
  handleClickCount(n) {
    const theCount = this.state.count + n;
    this.setState({ count: theCount });
  }
  render() {
    return (
      <div>
        <h1>React-Count-app</h1>
        <p>現在のカウンター：{this.state.count}</p>
        <button
          onClick={() => {
            this.handleClickCount(1);
          }}
        >
          カウントアップ
        </button>
        <br />
        <button
          onClick={() => {
            this.handleClickCount(-1);
          }}
        >
          カウントダウン
        </button>
      </div>
    );
  }
}

export default App;
