import React from "react";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      count: 0,
    };
  }
  ClickCount(n) {
    const theCount = this.state.count + n;
    this.setState({ count: theCount });
  }
  render() {
    return (
      <div>
        <h1>React-Count-app</h1>
        <p>現在のカウンター：{this.state.count}</p>
        <button
          onClick={() => {
            this.ClickCount(1);
          }}
        >
          カウントアップ
        </button>
        <br />
        <button
          onClick={() => {
            this.ClickCount(-1);
          }}
        >
          カウントダウン
        </button>
      </div>
    );
  }
}

export default App;
