import React from "react";
import Button from "@material-ui/core/Button";
import IconButton from "@material-ui/core/IconButton";
import DeleteIcon from "@material-ui/icons/Delete";

const App = () => {
  return <CountUp />;
};

class CountUp extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      count: 0,
    };
  }
  ClickCount(n) {
    const theCount = this.state.count + n;
    this.setState({ count: theCount });
  }
  ClickCountReset() {
    this.setState({ count: 0 });
  }
  render() {
    return (
      <div class="counter">
        <h1 class="counter-tittle">Reactカウンター</h1>
        <Button variant="contained" class="counter-button p10">
          Default
        </Button>
        <Button variant="contained" color="primary">
          Primary
        </Button>
        <div class="counter-value-wrap">
          現在のカウント
          <br />
          <span class="counter-value">{this.state.count}</span>
        </div>
        <Button
          class="counter-button p10"
          onClick={() => {
            this.ClickCount(10);
          }}
        >
          +10
        </Button>
        <button
          class="counter-button p1"
          onClick={() => {
            this.ClickCount(1);
          }}
        >
          +
        </button>
        <button
          class="counter-button m1"
          onClick={() => {
            this.ClickCount(-1);
          }}
        >
          -
        </button>
        <button
          class="counter-button m10"
          onClick={() => {
            this.ClickCount(-10);
          }}
        >
          -10
        </button>
        <IconButton
          aria-label="delete"
          onClick={() => {
            this.ClickCountReset();
          }}
        >
          <DeleteIcon />
        </IconButton>
      </div>
    );
  }
}

export default App;
